import React, { useEffect} from 'react'
import auth0 from "../lib/auth0"
import router from 'next/router'
import { db } from '../lib/db'
//useEffect é para criar uma função
//router é como <href>

/*Na tela vai pegar o retorno do login quando não
não conectado*/
const App = props =>{
    useEffect(() => {
        if(!props.isAuth){
            router.push('/')
        }else if(props.forceCreate){
            router.push('/create-status')
        }
        if(!props.isAuth || props.forceCreate){
            return null
        }
    })
    return(
        <div>
            <h1>App</h1>
            <pre>{JSON.stringify(props, null, 2)}</pre>
        </div>
    )
}
export default App


//Servidor que recebe a validação da conexão, não vai para o frontEnd
export async function getServerSideProps ({ req, res }){
    const session = await auth0.getSession(req)
    const today = new Date()//linha 9 e 10 para pegar a data
        const currentDate = today.getFullYear() + '-' + today.getMonth() + '-' + today.getDay()
    if (session){//aqui existe o usuário
        const todaysCheckin = await db
                .collection("markers")
                .doc(currentDate)
                .collection("checks")
                .doc(user.sub)
                .get();
        const todaysData = todaysCheckin.data()
        //variavel para constrolar se tem checkins
        let forceCreate = true//começa verdadeiro
        if(todaysData){
            // se tem se torna false
            //permanecer true não tem
            forceCreate = false
        }

        return{
            props:{
                //caso a pessoa não loge
                //se user for verdadeiro
                isAuth: true,
                user: session.user,
                forceCreate
            }
        }
    }
    return{
        props:{//Caso a pessoa não logou volta falso
            isAuth: false,
            user: {}
        }
    }
}
