import { db } from '../../lib/db'
import admin from 'firebase-admin'

const salveStatus = async(request, response) => {
   await db
    .collection('test')
    .add({
        test: 1,
        coordinates: new admin.firestore.GeoPoint(-20.21899,-45.93906)
    })
    console.log(request)
    response.send({ ok:true })
}
export default salveStatus
