      const checkins = await db.collection('markers').doc(currentDate).collection('checks') //verifica os próximos
      .near({
        center: todaysData.coordinates,
        radius: 1000 //mil metros

      }).get(); //varrer os arquivos para pegar os dados.

      const checkinsList = [];
      checkins.docs.forEach(doc => {
        checkinsList.push({
          id: doc.id,
          status: doc.data().status,
          coords: {
            lat: doc.data().coordinates.latitude,
            long: doc.data().coordinates.loangitude
          }
        });
      });
      return {
        props: {
          isAuth: true,
          user: session.user,
          forceCreate: false,
          checkins: checkinsList
        }
      };
      return {
        props: {
          //caso a pessoa não loge
          //se user for verdadeiro
          isAuth: true,
          user: session.user,
          forceCreate
        }
      };
    }

    return {
      props: {
        //Caso a pessoa não logou volta falso
        isAuth: false,
        user: {}
      }
    };
  }
}
