import React, { useState }from 'react'
import auth0 from '../lib/auth0'

const CreateStatus = () =>{
    const [dados, setDados] = useState({
        status: 'bem',
        coords:{
            lat:null,
            long:null
        }
    })
    const getMyLocation = () => {
        if(navigator.geolocation){
            navigator.geolocation.getCurrentPosition(position =>{
                console.log(position)
            })
        }
    }
    return (
        <div>
            <h1>Como você está? </h1>
            <label className='block'><input type='radio' name= 'status' value='bem'/>
                Estou bem e nunhum sintoma.</label>
            <label className='block'><input type='radio' name= 'status' value='gripe'/>
                Estou com sistomas de gripe/resfriado.</label>
            <label className='block'><input type='radio' name= 'covi-19' value='bem'/>
                Estou com sistomas de covid-19.</label>
            
            <button onClick={getMyLocation}>Minha Localização</button>
        </div>
    )
    
}
export default CreateStatus

//verifica que está logado (servidor)
export async function getServerSideProps ({ req, res }){
    const session = await auth0.getSession(req)

    if(session){
//return retorna quando está logado
    return{
        props:{
            //caso a pessoa não loge
            //se user for verdadeiro
            isAuth: true,
            user: session.user
        }
    }
}
//Quando não logado
    return{
        props:{//Caso a pessoa não logou volta falso
            isAuth: false,
            user: {}
        }
    }
}
