import { db } from '../../lib/db';
import admin from 'firebase-admin';

const salveStatus = async (request, response) => {
  const dados = request.body;
  const today = new Date();
  const currentDate = today.getFullYear() + '-' + today.getMonth() + '-' + today.getDay();
  await db.collection('markers').doc(currentDate).collection('checks').doc().add({
    status: dados.status,
    coordinates: new admin.firestore.GeoPoint(dados.coords.lat, dados.coords.long)
  });
  console.log(request);
  response.send({
    ok: true
  });
};

export default salveStatus;
